class Solution(object):    def moveZeroes(self, nums):        """        :type nums: List[int]        :rtype: void Do not return anything, modify nums in-place instead.        """        pos = 0        size = len(nums)                for i in range(size):            if nums[i] != 0:                nums[pos] = nums[i]                pos += 1                        while pos < size:            nums[pos] = 0            pos += 1            