import reclass Solution(object):    def isPalindrome(self, s):        """        :type s: str        :rtype: bool        """        letters = []        words = s.lower()        words = re.findall('[a-zA-Z0-9]+', words)        for word in words:            letters.extend(list(word))        size = len(letters)        if (size % 2) == 0:            loop_size = size//2        else:            loop_size = size//2 + 1        for idx in range(loop_size):            if letters[idx] != letters[size-1]:                return False            size -= 1        return True